	<script type="text/javascript">
		// Fuctions to change size and line heigth
		function changeFontSizeGBEN(newValue) {
		document.getElementById("changedfontsizeGBEN").innerHTML=newValue;
			$('.changesizeGBEN').css('font-size',newValue + 'px');
		};
		function changeLineHeigthGBEN(newValue) {
		document.getElementById("changedlineheigthGBEN").innerHTML=newValue;
			$('.changesizeGBEN').css('line-height', newValue);
		};
		// Fuctions to change capitalization of words
		function makeLowercaseGBEN() {
			$('.changesizeGBEN').removeClass('uppercase capitalized'); 
			$('.changesizeGBEN').addClass('lowercase');
		};	
		function makeCapitalizedGBEN() {
			$('.changesizeGBEN').removeClass('uppercase lowercase');
			$('.changesizeGBEN').addClass('capitalized');
		};	
		function makeUppercaseGBEN() {
			$('.changesizeGBEN').removeClass('lowercase capitalized');
			$('.changesizeGBEN').addClass('uppercase');
		};						
	</script>
